    private void btnCliqueActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCliqueActionPerformed
        Date relogio = new Date();
        String data = new SimpleDateFormat("dd/MM/yyyy").format(relogio);
        String hora = new SimpleDateFormat("HH:mm:ss").format(relogio);
        lblHora.setText(hora.toString());
        lblData.setText(data.toString());
    }//GEN-LAST:event_btnCliqueActionPerformed

